import React, { useEffect, useState } from "react";

const OwnersList = () => {
  const [owners, setOwners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [editOwner, setEditOwner] = useState(null);

  useEffect(() => {
    fetchOwners();
  }, []);

  const fetchOwners = () => {
    setLoading(true);
    fetch("http://localhost:5000/owners_list", { credentials: "include" })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) setOwners(data.owners);
        else setError(data.message || "Failed to fetch owners");
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError("Error fetching owners");
        setLoading(false);
      });
  };

  const handleDelete = async (owner_code) => {
    if (!window.confirm("Are you sure you want to delete this owner?")) return;

    try {
      const res = await fetch(`http://localhost:5000/delete_owner`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ owner_code }),
      });
      const data = await res.json();
      if (data.success) {
        alert("Owner deleted successfully");
        fetchOwners();
      } else alert(data.message || "Failed to delete owner");
    } catch (err) {
      console.error(err);
      alert("Error deleting owner");
    }
  };

  const currentDate = new Date().toLocaleDateString();

  const filteredOwners = owners.filter((owner) =>
    `${owner.first_name} ${owner.last_name} ${owner.email} ${owner.mobile_number} ${owner.nationality}`
      .toLowerCase()
      .includes(searchQuery.toLowerCase())
  );

  if (loading) return <p>Loading owners...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div style={{ fontFamily: "Arial, sans-serif", background: "gray", padding: "20px", minHeight: "100vh" }}>
      <div style={{ maxWidth: "1600px", margin: "auto", background: "white", padding: "15px", borderRadius: "8px", overflowX: "auto" }}>
        {/* Header */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", flexWrap: "wrap", marginBottom: "15px" }}>
          <div style={{ fontWeight: "bold", display: "flex", gap: "10px" }}>
            <span>{currentDate}</span> | <span>Total Owners: {filteredOwners.length}</span>
          </div>
          <h2 style={{ margin: "0 auto", color: "black" }}>List of Owners</h2>
          <div style={{ fontWeight: "bold" }}>
            <a href="/index" style={{ textDecoration: "none", color: "#333", fontWeight: "bold" }}>‚Üê Go Back Home</a>
          </div>
        </div>

        {/* Search */}
        <div style={{ marginBottom: "10px" }}>
          <input
            type="text"
            placeholder="Search owner..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            style={{ width: "100%", padding: "8px", fontSize: "0.9rem" }}
          />
        </div>

        {/* Owners Table */}
        <table style={{ width: "100%", borderCollapse: "collapse", borderRadius: "8px", overflow: "hidden" }}>
          <thead>
            <tr style={{ background: "#007BFF", color: "white", cursor: "pointer" }}>
              <th style={{ padding: "10px 12px" }}>Owner Code</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>Nationality</th>
              <th>EID</th>
              <th>Passport</th>
              <th>Visa</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {filteredOwners.map((owner) => (
              <React.Fragment key={owner.owner_code}>
                <tr style={{ borderBottom: "1px solid #ddd", fontSize: "0.9rem" }}>
                  <td style={{ padding: "10px 12px" }}>{`OW${owner.owner_code.toString().padStart(5, "0")}`}</td>
                  <td>{owner.first_name}</td>
                  <td>{owner.last_name}</td>
                  <td>{owner.email}</td>
                  <td>{owner.mobile_number}</td>
                  <td>{owner.nationality}</td>
                  <td>
                    {owner.eid_image ? (
                      <img src={`data:image/*;base64,${owner.eid_image}`} alt="EID" style={{ width: "30px", height: "30px", borderRadius: "4px", border: "1px solid #ccc", objectFit: "cover" }} />
                    ) : "N/A"}
                  </td>
                  <td>
                    {owner.passport_copy ? (
                      <img src={`data:image/*;base64,${owner.passport_copy}`} alt="Passport" style={{ width: "30px", height: "30px", borderRadius: "4px", border: "1px solid #ccc", objectFit: "cover" }} />
                    ) : "N/A"}
                  </td>
                  <td>
                    {owner.res_visa ? (
                      <img src={`data:image/*;base64,${owner.res_visa}`} alt="Visa" style={{ width: "30px", height: "30px", borderRadius: "4px", border: "1px solid #ccc", objectFit: "cover" }} />
                    ) : "N/A"}
                  </td>
                  <td>
                    <button onClick={() => setEditOwner(owner)} style={{ padding: "5px 10px", background: "blue", color: "white", border: "none", cursor: "pointer" }}>‚úè Edit</button>
                  </td>
                  <td>
                    <button onClick={() => handleDelete(owner.owner_code)} style={{ padding: "5px 10px", background: "red", color: "white", border: "none", cursor: "pointer" }}>üóë Delete</button>
                  </td>
                </tr>

                {/* Inline Edit Form */}
                {editOwner?.owner_code === owner.owner_code && (
                  <tr>
                    <td colSpan="11">
                      EditOwnerForm owner={editOwner} onClose={() => setEditOwner(null)} onSave={fetchOwners} />
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// ------------------- Inline Edit Form -------------------
// ------------------- Inline Edit Form (Bootstrap 4) -------------------
const EditOwnerForm = ({ owner, onClose, onSave }) => {
  const formatDate = (dateStr) => dateStr ? new Date(dateStr).toISOString().split("T")[0] : "";

  const [formData, setFormData] = useState({
    ...owner,
    date_of_birth: formatDate(owner.date_of_birth),
    visa_expiry: formatDate(owner.visa_expiry),
    passport_expiry: formatDate(owner.passport_expiry),
  });

  const [previews, setPreviews] = useState({
    eid_image: owner.eid_image || null,
    passport_copy: owner.passport_copy || null,
    res_visa: owner.res_visa || null,
  });

  const [files, setFiles] = useState({ eid_image: null, passport_copy: null, res_visa: null });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (["date_of_birth", "visa_expiry", "passport_expiry"].includes(name)) {
      const iso = new Date(value).toISOString().split("T")[0];
      setFormData((prev) => ({ ...prev, [name]: iso }));
    } else setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e, key) => {
    const file = e.target.files[0];
    if (!file) return;
    setFiles((prev) => ({ ...prev, [key]: file }));

    const reader = new FileReader();
    reader.onload = (ev) => setPreviews((prev) => ({ ...prev, [key]: ev.target.result }));
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    for (let key in formData) data.append(key, formData[key]);
    if (files.eid_image) data.append("OwnerEID", files.eid_image);
    if (files.passport_copy) data.append("OwnerPassportCopy", files.passport_copy);
    if (files.res_visa) data.append("OwnerResVisa", files.res_visa);
    data.append("owner_code", owner.owner_code);

    try {
      const res = await fetch("http://localhost:5000/update_owner", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();
      if (result.success) {
        alert("Owner updated successfully");
        onSave();
        onClose();
      } else alert(result.message || "Update failed");
    } catch (err) {
      console.error(err);
      alert("Error updating owner");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="border p-3 rounded bg-white">
      {/* Your form rows here, same as previous example */}
    </form>
  );
};

export default OwnersList;
