import React, { useState, useEffect } from "react";

const DeleteOwner = () => {
  const [ownerCode, setOwnerCode] = useState("");
  const [selectedOwner, setSelectedOwner] = useState(null);
  const [message, setMessage] = useState("");

  const [eidPreview, setEidPreview] = useState(null);
  const [passportPreview, setPassportPreview] = useState(null);
  const [visaPreview, setVisaPreview] = useState(null);

  // Search owner by code
  const handleSearch = async (e) => {
    e.preventDefault();
    setMessage("");
    setSelectedOwner(null);
    setEidPreview(null);
    setPassportPreview(null);
    setVisaPreview(null);

    try {
      const res = await fetch(`http://localhost:5000/owners/${ownerCode}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok) {
        setSelectedOwner(data);
      } else {
        setMessage(data.error || "Owner not found");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error fetching owner");
    }
  };

  // Fetch images as blobs for display
  useEffect(() => {
    const fetchImage = async (type, setter) => {
      if (!selectedOwner) return;
      const fileExists = type === "eid" ? selectedOwner.eid_image
                       : type === "passport" ? selectedOwner.passport_copy
                       : selectedOwner.res_visa;
      if (!fileExists) return;

      try {
        const res = await fetch(`http://localhost:5000/owner_image/${selectedOwner.owner_code}/${type}`, {
          credentials: "include",
        });
        if (!res.ok) throw new Error("Image fetch failed");
        const blob = await res.blob();
        setter(URL.createObjectURL(blob));
      } catch (err) {
        console.error(err);
      }
    };

    fetchImage("eid", setEidPreview);
    fetchImage("passport", setPassportPreview);
    fetchImage("visa", setVisaPreview);
  }, [selectedOwner]);

  // Delete owner confirmation
  const handleDelete = async (e) => {
    e.preventDefault();
    if (!window.confirm("Are you sure you want to delete this owner?")) return;

    try {
      const res = await fetch("http://localhost:5000/delete_owner", {
        method: "POST",
        credentials: "include",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ owner_code: selectedOwner.owner_code }),
      });
      const data = await res.json();
      if (res.ok) {
        setMessage("✅ Owner deleted successfully");
        setSelectedOwner(null);
        setOwnerCode("");
        setEidPreview(null);
        setPassportPreview(null);
        setVisaPreview(null);
      } else {
        setMessage(data.error || "❌ Error deleting owner");
      }
    } catch (err) {
      console.error(err);
      setMessage("❌ Error deleting owner");
    }
  };

  return (
    <div style={{ maxWidth: "1200px", margin: "auto", padding: "20px", background: "#fff", borderRadius: "8px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Delete Owner</h2>

      {message && (
        <p style={{ textAlign: "center", fontWeight: "bold", marginBottom: "15px", color: message.includes("✅") ? "green" : "red" }}>
          {message}
        </p>
      )}

      {/* Search Form */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", marginBottom: "20px" }}>
        <input
          type="text"
          value={ownerCode}
          onChange={(e) => setOwnerCode(e.target.value)}
          placeholder="Owner Code"
          required
          style={{ width: "120px", padding: "6px", fontSize: "0.85rem", marginRight: "5px" }}
        />
        <button type="submit" style={{ background: "#c0392b", color: "white", padding: "8px 16px", border: "none", borderRadius: "4px" }}>
          Search
        </button>
      </form>

      <a href="/" style={{ display: "block", textAlign: "center", marginBottom: "20px", color: "#333", fontWeight: "bold", textDecoration: "none" }}>
        ← Go Back Home
      </a>

      {/* Owner Details */}
      {selectedOwner && (
        <form onSubmit={handleDelete}>
          <div style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {["Salutation","First Name","Last Name","Nationality"].map((label,i)=>(
              <div key={i} style={{ width:"25%", padding:"0 10px", boxSizing:"border-box", marginBottom:"15px" }}>
                <label>{label}</label>
                <input type="text" value={selectedOwner[label.toLowerCase().replace(" ","_")]} readOnly style={{ width:"100%", padding:"8px", border:"1px solid #ccc", borderRadius:"4px" }} />
              </div>
            ))}
          </div>

          {/* Files */}
          <div style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {[
              ["Emirates ID File", eidPreview],
              ["Passport Copy File", passportPreview],
              ["Residence Visa File", visaPreview],
            ].map(([label, src], i)=>(
              <div key={i} style={{ width:"25%", padding:"0 10px", boxSizing:"border-box", marginBottom:"15px" }}>
                <label>{label}</label>
                {src && <img src={src} alt={label} style={{ display:"block", marginTop:"5px", maxWidth:"100px", maxHeight:"80px", border:"1px solid #ccc", borderRadius:"4px" }} />}
              </div>
            ))}
          </div>

          <div style={{ textAlign: "center" }}>
            <button type="submit" style={{ background: "#c0392b", color: "white", padding: "8px 16px", border: "none", borderRadius: "4px" }}>
              Delete Owner
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default DeleteOwner;
