import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const ListFinTrans = () => {
  const [transactions, setTransactions] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ------------------- Fetch All Transactions -------------------
  const fetchTransactions = async (query = "") => {
    setLoading(true);
    try {
      const url = query
        ? `http://localhost:5000/search_fintrans?query=${query}`
        : "http://localhost:5000/fintrans_list";

      const res = await fetch(url);
      const data = await res.json();

      if (res.ok && data.success) setTransactions(data.transactions || []);
      else setTransactions([]);
    } catch (err) {
      console.error("Error fetching transactions:", err);
      setTransactions([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTransactions();
  }, []);

  // ------------------- Handle Search -------------------
  const handleSearch = (e) => {
    e.preventDefault();
    fetchTransactions(search.trim());
  };

  // ------------------- Render -------------------
  return (
    <div style={{ padding: 20, fontFamily: "Arial, sans-serif" }}>
      <h2
        style={{
          textAlign: "center",
          background: "#2f3640",
          color: "white",
          padding: 15,
          marginBottom: 20,
          borderRadius: 8,
        }}
      >
        Financial Transactions List
      </h2>

      {/* Search Bar */}
      <form
        onSubmit={handleSearch}
        style={{
          textAlign: "center",
          marginBottom: 20,
        }}
      >
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Search by Property Code or Transaction Ref No"
          style={{
            width: 400,
            padding: 8,
            borderRadius: 6,
            border: "1px solid #aaa",
          }}
        />
        <button
          type="submit"
          style={{
            marginLeft: 10,
            padding: "8px 15px",
            background: "#273c75",
            color: "white",
            border: "none",
            borderRadius: 6,
            cursor: "pointer",
          }}
        >
          üîç Search
        </button>
      </form>

      {/* Transactions Table */}
      {loading ? (
        <p style={{ textAlign: "center" }}>Loading...</p>
      ) : transactions.length === 0 ? (
        <p style={{ textAlign: "center" }}>No transactions found.</p>
      ) : (
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            background: "white",
            borderRadius: 10,
            overflow: "hidden",
            boxShadow: "0 0 10px #ccc",
          }}
        >
          <thead style={{ background: "#273c75", color: "white" }}>
            <tr>
              <th style={th}>Ref No</th>
              <th style={th}>Property Code</th>
              <th style={th}>Tenant Name</th>
              <th style={th}>Receipt / Payment</th>
              <th style={th}>Reason</th>
              <th style={th}>Mode</th>
              <th style={th}>Amount</th>
              <th style={th}>Date</th>
              <th style={th}>Action</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((t) => (
              <tr key={t.FinTransRefNo}>
                <td style={td}>{t.FinTransRefNo}</td>
                <td style={td}>{t.PropertyCode}</td>
                <td style={td}>{t.TenantName || "-"}</td>
                <td style={td}>{t.ReceiptPayment}</td>
                <td style={td}>{t.ReceiptPaymentReason || "-"}</td>
                <td style={td}>{t.ModeOfPayment}</td>
                <td style={td}>{t.TrAmount}</td>
                <td style={td}>{t.TrDate}</td>
                <td style={td}>
                  <button
                    onClick={() => navigate(`/editfintrans/${t.FinTransRefNo}`)}
                    style={{
                      padding: "5px 10px",
                      background: "#44bd32",
                      color: "white",
                      border: "none",
                      borderRadius: 5,
                      cursor: "pointer",
                    }}
                  >
                    ‚úèÔ∏è Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

// ---------- Table Styles ----------
const th = {
  padding: "10px 8px",
  textAlign: "left",
  borderBottom: "2px solid #ddd",
};

const td = {
  padding: "8px",
  borderBottom: "1px solid #eee",
  textAlign: "left",
};

export default ListFinTrans;
