import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const ViewProperty = () => {
  const { property_code } = useParams();
  const navigate = useNavigate();
  const [property, setProperty] = useState(null);
  const [tenant, setTenant] = useState(null);
  const [loading, setLoading] = useState(true);

useEffect(() => {
  const fetchProperty = async () => {
    try {
      const res = await fetch(`http://localhost:5000/view_property/${property_code}`, { credentials: "include" });
      const data = await res.json();
      console.log(data); // <--- check here
      if (res.ok && data.success) {
        setProperty(data.property);
        setTenant(data.tenant);
      } else {
        alert(data.message || "Property not found");
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching property");
    } finally {
      setLoading(false);
    }
  };
  fetchProperty();
}, [property_code]);

  if (loading) return <p>Loading property details...</p>;
  if (!property) return <p>Property not found</p>;

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20 }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, borderRadius: 6 }}>
        Property Details
      </h2>

      <button
        onClick={() => navigate(-1)}
        style={{
          marginTop: 20,
          backgroundColor: "#7f8c8d",
          color: "#fff",
          padding: "8px 12px",
          border: "none",
          borderRadius: 6,
          cursor: "pointer",
          width:150
        }}
      >
        ← Back
      </button>

      <div style={{ display: "flex", gap: 30, marginTop: 20 }}>
       {property.property_photo ? (
  <img
    src={`data:image/jpeg;base64,${property.property_photo}`}
    alt="Property"
    style={{ width: "200px", height: "150px", objectFit: "cover", borderRadius: "10px" }}
  />
) : (
  <p>No property image available</p>
)}

        <div>
          <p><strong>Property Code:</strong> {property.property_code}</p>
          <p><strong>Name:</strong> {property.name}</p>
          <p><strong>Developer:</strong> {property.developer_name}</p>
          <p><strong>Building:</strong> {property.building_name}</p>
          <p><strong>Type:</strong> {property.property_type}</p>
          <p><strong>Address:</strong> {property.address1}, {property.city}, {property.state}, {property.country}</p>
          <p><strong>Bedrooms:</strong> {property.bedrooms} | <strong>Bathrooms:</strong> {property.bathrooms}</p>
          <p><strong>Car Parkings:</strong> {property.car_parkings}</p>
          <p><strong>Ownership:</strong> {property.ownership_type} | <strong>Furnishing:</strong> {property.furnishing_status}</p>
          <p><strong>Rent Value:</strong> {property.expected_rent_value}</p>
        </div>
      </div>

      {tenant ? (
        <div style={{ marginTop: 20 }}>
          <h3 style={{ color: "#27ae60" }}>Current Tenant</h3>
          <p><strong>Name:</strong> {tenant.tenantname}</p>
          <p><strong>Email:</strong> {tenant.tenantemailid}</p>
          <p><strong>Mobile:</strong> {tenant.tenantmobilenumber}</p>
          <p><strong>Lease:</strong> {tenant.leasestartdate?.slice(0, 10)} → {tenant.leaseenddate?.slice(0, 10)}</p>
          <p><strong>Rent:</strong> {tenant.rentamount}</p>
        </div>
      ) : (
        <p style={{ color: "gray", marginTop: 20 }}>No tenant currently assigned.</p>
      )}
    </div>
  );
};

export default ViewProperty;
