import React, { useState } from "react";

const ModifyOwner = () => {
  const [ownerCode, setOwnerCode] = useState("");
  const [ownerData, setOwnerData] = useState(null);
  const [eidPreview, setEidPreview] = useState(null);
  const [passportPreview, setPassportPreview] = useState(null);
  const [visaPreview, setVisaPreview] = useState(null);

const handleSearch = async (e) => {
  e.preventDefault();
  setOwnerData(null);
  setEidPreview(null);
  setPassportPreview(null);
  setVisaPreview(null);

  try {
    const res = await fetch(`http://localhost:5000/owners/${ownerCode}`, {
      method: "GET",
      credentials: "include",
    });
    const data = await res.json();
    if (res.ok && data.success) {
      const owner = data.owner;
      setOwnerData(owner);
      setEidPreview(owner.eid_image ? `http://localhost:5000/owner_image/${owner.owner_code}/eid_image` : null);
      setPassportPreview(owner.passport_copy ? `http://localhost:5000/owner_image/${owner.owner_code}/passport_copy` : null);
      setVisaPreview(owner.res_visa ? `http://localhost:5000/owner_image/${owner.owner_code}/res_visa` : null);
    } else {
      alert(data.message || "Owner not found");
    }
  } catch (err) {
    console.error(err);
    alert("Error fetching owner");
  }
};

  const handleFileChange = (e, type) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      if (type === "eid") setEidPreview(event.target.result);
      if (type === "passport") setPassportPreview(event.target.result);
      if (type === "visa") setVisaPreview(event.target.result);
    };
    reader.readAsDataURL(file);
  };
const formatDate = (dateStr) => {
  if (!dateStr) return "";
  const date = new Date(dateStr);
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
};

  const handleUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    try {
      const res = await fetch("http://localhost:5000/update_owner", {
        method: "POST",
        credentials: "include",
        body: formData,
      });
      const data = await res.json();
      if (res.ok) {
        alert(`✅ ${data.message || "Owner updated successfully"}`);
      } else {
        alert(`❌ ${data.message || "Error updating owner"}`);
      }
    } catch (err) {
      console.error(err);
      alert("❌ Error updating owner");
    }
  };

  return (
    <div className="container">
      <h2>Modify Owner</h2>

      <form onSubmit={handleSearch} style={{ textAlign: "center", marginBottom: "20px" }}>
        <label>Enter Owner Code:</label>
        <input
          type="text"
          value={ownerCode}
          onChange={(e) => setOwnerCode(e.target.value)}
          placeholder="Owner Code"
          required
          style={{ width: "120px", marginRight: "5px" }}
        />
        <button type="submit">Search</button>
      </form>

      <a href="/index" style={{ display: "block", textAlign: "center", marginBottom: "20px" }}>← Go Back Home</a>

      {ownerData && (
        <form onSubmit={handleUpdate} encType="multipart/form-data">
          <input type="hidden" name="owner_code" value={ownerData.owner_code} />

          {/* Row 1 */}
          <div className="row">
            {["salutation", "first_name", "last_name", "nationality"].map((field) => (
              <div key={field} className="col-md-3">
                <label>{field.replace("_", " ").replace(/\b\w/g, c => c.toUpperCase())}:</label>
                <input name={field} defaultValue={ownerData[field] || ""} type="text" />
              </div>
            ))}
          </div>

          {/* Row 2 */}
          <div className="row">
            {["address1", "address2", "city", "state"].map((field) => (
              <div key={field} className="col-md-3">
                <label>{field.replace("_", " ").replace(/\b\w/g, c => c.toUpperCase())}:</label>
                <input name={field} defaultValue={ownerData[field] || ""} type="text" />
              </div>
            ))}
          </div>

          {/* Row 3 */}
          <div className="row">
            {["country", "zip_code", "mobile_number", "email"].map((field) => (
              <div key={field} className="col-md-3">
                <label>{field.replace("_", " ").replace(/\b\w/g, c => c.toUpperCase())}:</label>
                <input name={field} defaultValue={ownerData[field] || ""} type={field.includes("email") ? "email" : "text"} />
              </div>
            ))}
          </div>

          {/* Row 4 */}
          <div className="row">
            {["passport_number", "date_of_birth", "visa_expiry_date", "passport_expiry_date"].map((field) => (
              <div key={field} className="col-md-3">
                <label>{field.replace("_", " ").replace(/\b\w/g, c => c.toUpperCase())}:</label>
<input
        name={field}
        defaultValue={formatDate(ownerData[field])} // ✅ formatted
        type="date"
      />              </div>
            ))}
          </div>

          {/* Row 5 - Files */}
          <div className="row">
            <div className="col-md-4">
              <label>Emirates ID:</label>
              <input type="file" name="OwnerEID" onChange={(e) => handleFileChange(e, "eid")} />
              {eidPreview && <img src={eidPreview} alt="EID Preview" style={{ maxWidth: "100px", maxHeight: "80px" }} />}
            </div>
            <div className="col-md-4">
              <label>Passport Copy:</label>
              <input type="file" name="OwnerPassportCopy" onChange={(e) => handleFileChange(e, "passport")} />
              {passportPreview && <img src={passportPreview} alt="Passport Preview" style={{ maxWidth: "100px", maxHeight: "80px" }} />}
            </div>
            <div className="col-md-4">
              <label>Residence Visa:</label>
              <input type="file" name="OwnerResVisa" onChange={(e) => handleFileChange(e, "visa")} />
              {visaPreview && <img src={visaPreview} alt="Visa Preview" style={{ maxWidth: "100px", maxHeight: "80px" }} />}
            </div>
          </div>

          {/* Row 6 - Created By & Approved By */}

          <div style={{ textAlign: "center", marginTop: "10px" }}>
            <button type="submit">Update Owner</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default ModifyOwner;
