import React, { useEffect, useState } from "react";

const ListProperty = () => {
  const [properties, setProperties] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/get_properties", { credentials: "include" });
      const data = await res.json();
      setProperties(data.properties || []);
    } catch (err) {
      console.error(err);
      alert("Error fetching properties");
    } finally {
      setLoading(false);
    }
  };

  const handleSort = (key) => {
    const sorted = [...properties].sort((a, b) => {
      const valA = a[key] ?? "";
      const valB = b[key] ?? "";
      if (!isNaN(valA) && !isNaN(valB)) return valA - valB;
      return valA.toString().localeCompare(valB.toString());
    });
    setProperties(sorted);
  };

  const filteredProperties = properties.filter(
    (p) =>
      p.PropertyCode?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      p.Name?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const labelize = (s) => s.replace(/([A-Z])/g, " $1").replace(/_/g, " ").replace(/\b\w/g, ch => ch.toUpperCase()).trim();

  const photoFields = ["property_photo"]; // add any additional photo fields here

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f0f2f5" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0, borderRadius: "8px 8px 0 0" }}>
        Property List
      </h2>

      <div style={{ maxWidth: 1500, margin: "20px auto", background: "white", padding: 20, borderRadius: 10, boxShadow: "0 0 15px rgba(0,0,0,0.2)", overflowX: "auto" }}>
        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 15 }}>
          <div>
            <span>{new Date().toLocaleDateString()}</span> | Total Properties: {filteredProperties.length}
          </div>
          <div>
            <input
              type="text"
              placeholder="Search by Code or Name"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{ padding: 6, borderRadius: 5, border: "1px solid #ccc" }}
            />
            <a href="/index" style={{ textDecoration: "none", marginLeft: 20, fontWeight: "bold", color: "#333" }}>‚Üê Go Back Home</a>
          </div>
        </div>

        {loading ? <p>Loading properties...</p> : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {properties[0] && Object.keys(properties[0]).map((col) => (
                  <th
                    key={col}
                    onClick={() => handleSort(col)}
                    style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white", cursor: "pointer" }}
                  >
                    {labelize(col)}
                  </th>
                ))}
                <th style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredProperties.map((p, idx) => (
                <tr key={idx} style={{ backgroundColor: idx % 2 === 0 ? "#f2f2f2" : "#fff" }}>
                  {Object.keys(p).map((key) => (
                    <td key={key} style={{ padding: 8, border: "1px solid #ccc", textAlign: "center" }}>
                      {photoFields.includes(key) && p[key] ? (
                        <img src={`data:image/jpeg;base64,${p[key]}`} alt={key} style={{ width: 60, height: 60, borderRadius: 6, border: "1px solid #ccc" }} />
                      ) : (
                        p[key] ?? ""
                      )}
                    </td>
                  ))}
                  <td style={{ padding: 8, border: "1px solid #ccc", textAlign: "center" }}>
                    <button style={{ marginRight: 6, padding: "6px 12px", background: "#2980b9", color: "#fff", border: "none", borderRadius: 4, cursor: "pointer" }}>Edit</button>
                    <button style={{ padding: "6px 12px", background: "#c0392b", color: "#fff", border: "none", borderRadius: 4, cursor: "pointer" }}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default ListProperty;
