import React, { useState } from "react";

const ModifyTenant = ({ sessionUser }) => {
  const [tenancyId, setTenancyId] = useState("");
  const [tenant, setTenant] = useState(null);
  const [formData, setFormData] = useState({});
  const [previews, setPreviews] = useState({});
  const [loading, setLoading] = useState(false);

  const photoFields = [
    "PassportCopyPath",
    "EIDCopyPath",
    "ResidenceVisaCopyPath",
    "BankStatementCopyPath",
    "DepositChequeCopyPath",
    "SecurityChequeCopyPath",
    "EjariMunicipalRegistrationCopyPath",
  ];

  const dateFields = [
    "TenantDOB",
    "TenantPassportExpiryDate",
    "TenantEIDExpiryDate",
    "LeaseStartDate",
    "LeaseEndDate",
    "MoveInDate",
    "MoveOutDate",
    "CreationDate",
  ];

  const parseDate = (val) => {
    if (!val) return "";
    const d = new Date(val);
    if (isNaN(d)) return "";
    const month = String(d.getMonth() + 1).padStart(2, "0");
    const day = String(d.getDate()).padStart(2, "0");
    return `${d.getFullYear()}-${month}-${day}`;
  };

  const handleSearch = async (e, idParam) => {
    e?.preventDefault();
    const id = idParam || tenancyId;
    if (!id) return alert("Please enter a tenancy ID");

    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/modify_tenant/search", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ tenancyContractNumber: id }),
      });
      const data = await res.json();

      if (res.ok && data.success) {
        setTenant(data.tenant);
        const initial = {};
        Object.keys(data.tenant).forEach((k) => {
          if (!photoFields.includes(k)) {
            initial[k] = dateFields.includes(k) ? parseDate(data.tenant[k]) : data.tenant[k] ?? "";
          }
        });
        setFormData(initial);

        // Set image previews
        const p = {};
        photoFields.forEach((f) => {
          if (data.tenant[f]) {
            p[f] = data.tenant[f].startsWith("data:")
              ? data.tenant[f]
              : `data:image/jpeg;base64,${data.tenant[f]}`;
          } else p[f] = null;
        });
        setPreviews(p);
      } else {
        alert(data.message || "Tenant not found");
        setTenant(null);
        setFormData({});
        setPreviews({});
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching tenant");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e, key) => {
    const file = e.target.files[0];
    if (!file) return;
    setFormData((prev) => ({ ...prev, [key]: file }));
    if (file.type.startsWith("image/")) {
      const reader = new FileReader();
      reader.onload = (ev) => setPreviews((prev) => ({ ...prev, [key]: ev.target.result }));
      reader.readAsDataURL(file);
    } else setPreviews((prev) => ({ ...prev, [key]: null }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!tenant) return alert("Please search a tenant first");

    const data = new FormData();
    data.append("tenancy_contract_number", tenant.TenancyContractNumber || tenancyId);

    Object.entries(formData).forEach(([key, val]) => {
      if (val instanceof File) data.append(key, val, val.name);
      else if (val != null) data.append(key, val);
    });

    if (!formData.created_by) data.append("created_by", sessionUser || "admin");
    if (!formData.creation_date) data.append("creation_date", new Date().toISOString().slice(0, 10));

    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/modify_tenant/update", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();
      if (res.ok && result.success) {
        alert(`✅ ${result.message}`);
        await handleSearch({ preventDefault: () => {} }, tenant.TenancyContractNumber || tenancyId);
      } else alert(`❌ ${result.message || "Failed to update tenant"}`);
    } catch (err) {
      console.error(err);
      alert("❌ Error updating tenant");
    } finally {
      setLoading(false);
    }
  };

  const labelize = (s) =>
    s.replace(/([A-Z])/g, " $1")
      .replace(/_/g, " ")
      .replace(/\b\w/g, (ch) => ch.toUpperCase())
      .trim();

  const getValue = (key) => formData[key] ?? tenant?.[key] ?? "";

  const rows = [
    ["tenant_name", "tenant_dob", "tenant_nationality", "tenant_passport"],
    ["tenant_eid", "passport_expiry", "eid_expiry", "tenant_employer", "tenant_mobile"],
    ["tenant_email", "lease_start", "lease_end", "move_in", "move_out"],
    ["rent_amount", "deposit_amount", "number_of_payments", "rent_received", "rent_outstanding"],
    ["created_by", "creation_date"],
  ];

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20 }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0 }}>
        Modify Tenant
      </h2>

      {/* Search */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", margin: "20px 0" }}>
        <label style={{ fontWeight: "bold", marginRight: 8 }}>Enter Tenant ID:</label>
        <input
          type="text"
          value={tenancyId}
          onChange={(e) => setTenancyId(e.target.value)}
          placeholder="Ex: 1"
          required
          style={{ width: 180, padding: 8, borderRadius: 6, border: "1px solid #aaa" }}
        />
        <button type="submit" style={{ marginLeft: 8, padding: "8px 16px", background: "#273c75", color: "white", border: "none", borderRadius: 6, cursor: "pointer" }}>
          {loading ? "Searching..." : "Search"}
        </button>
      </form>

      {tenant && (
        <form onSubmit={handleUpdate} encType="multipart/form-data" style={{ maxWidth: 1400, margin: "0 auto", background: "white", padding: 20, borderRadius: 10, boxShadow: "0 0 12px #ccc" }}>
          {rows.map((cols, i) => (
            <div key={i} style={{ display: "grid", gridTemplateColumns: `repeat(${cols.length}, 1fr)`, gap: 20, marginBottom: 20 }}>
              {cols.map((field) => {
                const isDate = dateFields.includes(field);
                const readOnly = ["tenancy_contract_number", "rent_received", "rent_outstanding"].includes(field);
                const value = getValue(field);
                return (
                  <div key={field}>
                    <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>{labelize(field)}</label>
                    <input
                      type={isDate ? "date" : "text"}
                      name={field}
                      value={value || ""}
                      onChange={handleChange}
                      readOnly={readOnly}
                      style={{
                        width: "100%",
                        padding: 8,
                        borderRadius: 6,
                        border: "1px solid #ccc",
                        background: readOnly ? "#f3f3f3" : "white",
                      }}
                    />
                  </div>
                );
              })}
            </div>
          ))}

          {/* Photos */}
          <div style={{ display: "flex", flexWrap: "wrap", gap: 15, marginTop: 20, justifyContent: "center" }}>
            {photoFields.map((field) => (
              <div key={field} style={{ textAlign: "center" }}>
                <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>{field}</label>
                {previews[field] ? (
                  <img src={previews[field]} alt={field} style={{ width: 70, height: 70, objectFit: "cover", borderRadius: 6, border: "1px solid #ccc", marginBottom: 6 }} />
                ) : (
                  <p style={{ margin: "6px 0" }}>N/A</p>
                )}
                <input type="file" name={field} onChange={(e) => handleFileChange(e, field)} />
              </div>
            ))}
          </div>

          <div style={{ textAlign: "center", marginTop: 30 }}>
            <a href="/index" style={{ ...styles.button, backgroundColor: "#492bc0ff", marginRight: 20, textDecoration: "none", display: "inline-block" }}>← Go Back Home</a>
            <button type="submit" style={styles.deleteButton}>Modify Tenant</button>
          </div>
        </form>
      )}
    </div>
  );
};

const styles = {
  button: { width: 200, padding: "10px 20px", backgroundColor: "#0097e6", color: "white", border: "none", borderRadius: 6, cursor: "pointer", fontSize: 16 },
  deleteButton: { width: 200, padding: "10px 20px", backgroundColor: "#c0392b", color: "white", border: "none", borderRadius: 6, cursor: "pointer", fontSize: 16 },
};

export default ModifyTenant;
