// TenantList.jsx
import React, { useEffect, useState } from "react";

const TenantList = () => {
  const [tenants, setTenants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [previewSrc, setPreviewSrc] = useState(null);

  const [editingTenant, setEditingTenant] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [editFiles, setEditFiles] = useState({});
  const [editLoading, setEditLoading] = useState(false);

  // üîπ Sort config
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "asc" });

  const columnOrder = [
    "TenancyContractNumber",
    "PropertyCode",
    "TenantName",
    "TenantDOB",
    "TenantNationality",
    "TenantPassportNumber",
    "TenantEmiratesID",
    "TenantPassportExpiryDate",
    "TenantEIDExpiryDate",
    "TenantEmployer",
    "TenantMobileNumber",
    "TenantEmailID",
    "LeaseStartDate",
    "LeaseEndDate",
    "MoveInDate",
    "MoveOutDate",
    "RentAmount",
    "DepositAmountReceived",
    "NumberOfPayments",
    "RentAmountReceived",
    "RentAmountOutstanding",
    "PassportCopyPath",
    "EIDCopyPath",
    "ResidenceVisaCopyPath",
    "BankStatementCopyPath",
    "DepositChequeCopyPath",
    "SecurityChequeCopyPath",
    "EjariMunicipalRegistrationCopyPath",
    "CreatedBy",
    "CreationDate",
  ];

  const photoFields = [
    "PassportCopyPath",
    "EIDCopyPath",
    "ResidenceVisaCopyPath",
    "BankStatementCopyPath",
    "DepositChequeCopyPath",
    "SecurityChequeCopyPath",
    "EjariMunicipalRegistrationCopyPath",
  ];

  const dateFields = [
    "TenantDOB",
    "TenantPassportExpiryDate",
    "TenantEIDExpiryDate",
    "LeaseStartDate",
    "LeaseEndDate",
    "MoveInDate",
    "MoveOutDate",
    "CreationDate",
  ];

  const dbToFormMap = {
    TenantName: "tenant_name",
    TenantDOB: "tenant_dob",
    TenantNationality: "tenant_nationality",
    TenantPassportNumber: "tenant_passport",
    TenantEmiratesID: "tenant_eid",
    TenantPassportExpiryDate: "passport_expiry",
    TenantEIDExpiryDate: "eid_expiry",
    TenantEmployer: "tenant_employer",
    TenantMobileNumber: "tenant_mobile",
    TenantEmailID: "tenant_email",
    LeaseStartDate: "lease_start",
    LeaseEndDate: "lease_end",
    MoveInDate: "move_in",
    MoveOutDate: "move_out",
    RentAmount: "rent_amount",
    DepositAmountReceived: "deposit_amount",
    NumberOfPayments: "number_of_payments",
    CreatedBy: "created_by",
    CreationDate: "creation_date",
  };

  useEffect(() => {
    fetchTenants();
  }, []);

  async function fetchTenants() {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/tenant_list", {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) setTenants(data.tenants || []);
      else alert(data.message || "Failed to fetch tenants");
    } catch (err) {
      console.error(err);
      alert("Error fetching tenants");
    } finally {
      setLoading(false);
    }
  }
// Add these inside TenantList component (before return)
// Open edit modal and prepare form
const openEditModal = (tenant) => {
  console.log("openEditModal called for", tenant?.TenancyContractNumber ?? tenant); // debug line

  // defensive guard
  if (!tenant) {
    console.warn("openEditModal: no tenant provided");
    return;
  }

  // Build the edit form using the dbToFormMap you already have
  const form = {};
  for (const [dbCol, formKey] of Object.entries(dbToFormMap)) {
    const val = tenant[dbCol];
    // If value looks like an ISO date or has slice, normalize to yyyy-mm-dd
    form[formKey] = val ? (typeof val === "string" && val.length >= 10 ? val.slice(0, 10) : val) : "";
  }

  // include tenancy_contract_number expected by backend
  form.tenancy_contract_number = tenant.TenancyContractNumber;

  setEditForm(form);
  setEditingTenant(tenant);
  setEditFiles({});

  // optional: scroll modal into view or focus first input after a tick
  // setTimeout(() => document.querySelector("input[name='tenant_name']")?.focus(), 50);
};

const closeEditModal = () => {
  setEditingTenant(null);
  setEditForm({});
  setEditFiles({});
};

const handleDeleteApi = async (tenancyNumber) => {
  if (!window.confirm(`Delete tenant ${tenancyNumber}?`)) return;
  try {
    const res = await fetch(`http://localhost:5000/tenant_delete_api/${tenancyNumber}`, {
      method: "DELETE",
      credentials: "include",
    });
    const data = await res.json();
    if (res.ok && data.success) {
      alert("Tenant deleted successfully");
      fetchTenants(); // refresh list
    } else {
      alert(data.message || "Failed to delete tenant");
    }
  } catch (err) {
    console.error(err);
    alert("Error deleting tenant");
  }
};

  // üîπ Handle sorting
  const handleSort = (key) => {
    setSortConfig((prev) => {
      if (prev.key === key) {
        // toggle direction
        return { key, direction: prev.direction === "asc" ? "desc" : "asc" };
      }
      return { key, direction: "asc" };
    });
  };

  // üîπ Filter + Sort tenants
  const filtered = tenants.filter((t) => {
    const q = searchTerm.trim().toLowerCase();
    if (!q) return true;
    return (
      (t.TenantName || "").toString().toLowerCase().includes(q) ||
      (t.TenancyContractNumber || "").toString().toLowerCase().includes(q)
    );
  });

  const sorted = React.useMemo(() => {
    if (!sortConfig.key) return filtered;
    return [...filtered].sort((a, b) => {
      const key = sortConfig.key;
      let valA = a[key];
      let valB = b[key];

      // Handle nulls and undefined
      if (valA == null) return sortConfig.direction === "asc" ? -1 : 1;
      if (valB == null) return sortConfig.direction === "asc" ? 1 : -1;

      // Convert numbers
      if (!isNaN(valA) && !isNaN(valB)) {
        valA = Number(valA);
        valB = Number(valB);
      } else {
        // Compare as strings
        valA = valA.toString().toLowerCase();
        valB = valB.toString().toLowerCase();
      }

      if (valA < valB) return sortConfig.direction === "asc" ? -1 : 1;
      if (valA > valB) return sortConfig.direction === "asc" ? 1 : -1;
      return 0;
    });
  }, [filtered, sortConfig]);

  const renderThumb = (t, key) => {
    const val = t[key];
    if (!val) return <div style={{ fontSize: 12, color: "#666" }}>‚Äî</div>;
    const src = val.startsWith("data:") ? val : `data:image/jpeg;base64,${val}`;
    return (
      <img
        src={src}
        alt={key}
        style={{
          width: 48,
          height: 48,
          objectFit: "cover",
          borderRadius: 4,
          border: "1px solid #ccc",
          cursor: "pointer",
        }}
        onClick={() => setPreviewSrc(src)}
      />
    );
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f0f2f5" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0 }}>
        Tenant List
      </h2>

      <div
        style={{
          maxWidth: 1500,
          margin: "20px auto",
          background: "white",
          padding: 16,
          borderRadius: 8,
          boxShadow: "0 0 12px rgba(0,0,0,0.12)",
          overflowX: "auto",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: 12,
          }}
        >
          <div style={{ fontWeight: "bold" }}>
            {new Date().toLocaleDateString()} | Total Tenants: {tenants.length}
          </div>

          <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
            <input
              placeholder="Search by name or Tenant number"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{
                padding: "8px 12px",
                borderRadius: 6,
                border: "1px solid #ccc",
                width: 350,
              }}
            />
          </div>

          <div style={{ fontWeight: "bold" }}>
            <a
              href="/index"
              style={{
                backgroundColor: "gray",
                textDecoration: "none",
                color: "#333",
                fontWeight: "bold",
                padding: "8px 12px",
                borderRadius: 6,
                cursor: "pointer",
                width: 300,
              }}
            >
              ‚Üê Go Back Home
            </a>
          </div>
        </div>

        {loading ? (
          <p>Loading...</p>
        ) : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {columnOrder.map((col) => (
                  <th
                    key={col}
                    onClick={() => handleSort(col)}
                    style={{
                      padding: 8,
                      border: "1px solid #ddd",
                      background: "#273c75",
                      color: "#fff",
                      fontSize: 12,
                      cursor: "pointer",
                      userSelect: "none",
                    }}
                  >
                    {col}{" "}
                    {sortConfig.key === col &&
                      (sortConfig.direction === "asc" ? "‚ñ≤" : "‚ñº")}
                  </th>
                ))}
                <th
                  style={{
                    padding: 8,
                    border: "1px solid #ddd",
                    background: "#273c75",
                    color: "#fff",
                    fontSize: 12,
                  }}
                >
                  Actions
                </th>
              </tr>
            </thead>

            <tbody>
              {sorted.map((t, idx) => (
                <tr
                  key={t.TenancyContractNumber || idx}
                  style={{
                    background: idx % 2 === 0 ? "#fafafa" : "#fff",
                  }}
                >
                  {columnOrder.map((key) => (
                    <td
                      key={key}
                      style={{
                        padding: 6,
                        border: "1px solid #eee",
                        textAlign: "center",
                        fontSize: 13,
                      }}
                    >
                      {photoFields.includes(key)
                        ? renderThumb(t, key)
                        : dateFields.includes(key) && t[key]
                        ? t[key].slice(0, 10)
                        : t[key] ?? ""}
                    </td>
                  ))}
                  <td
                    style={{
                      padding: 8,
                      border: "1px solid #eee",
                      display: "flex",
                      gap: 8,
                      justifyContent: "center",
                    }}
                  >
                    <button
                      onClick={() => openEditModal(t)}
                      style={{
                        padding: "6px 10px",
                        borderRadius: 6,
                        cursor: "pointer",
                      }}
                    >
                      ‚úèÔ∏è Edit
                    </button>
                    <button
                      onClick={() => handleDeleteApi(t.TenancyContractNumber)}
                      style={{
                        padding: "6px 10px",
                        borderRadius: 6,
                        background: "#c0392b",
                        color: "#fff",
                        cursor: "pointer",
                      }}
                    >
                      üóëÔ∏è Delete
                    </button>
                  </td>
                </tr>
              ))}
              {sorted.length === 0 && (
                <tr>
                  <td colSpan={columnOrder.length + 1} style={{ textAlign: "center", padding: 16 }}>
                    No tenants found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        )}
      </div>

      {/* Image Preview */}
      {previewSrc && (
        <div
          onClick={() => setPreviewSrc(null)}
          style={{
            position: "fixed",
            inset: 0,
            background: "rgba(0,0,0,0.8)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 2000,
          }}
        >
          <img
            src={previewSrc}
            alt="preview"
            style={{
              maxWidth: "90%",
              maxHeight: "90%",
              borderRadius: 6,
            }}
          />
        </div>
      )}
    </div>
  );
};

export default TenantList;
