import React, { useState, useEffect } from "react";

const ListOwners = () => {
  const [owners, setOwners] = useState([]);
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [currentDate, setCurrentDate] = useState("");
  const [previewImage, setPreviewImage] = useState(null);

  const columns = [
    "owner_code", "salutation", "first_name", "last_name", "nationality",
    "address1", "address2", "city", "state", "country", "zip_code",
    "mobile_number", "email", "passport_number", "emirates_id",
    "date_of_birth", "visa_expiry_date", "passport_expiry_date",
    "eid_image", "passport_copy", "res_visa"
  ];

  useEffect(() => {
    fetchOwners();
    setCurrentDate(new Date().toLocaleDateString());
  }, [sortField, sortOrder]);

  const fetchOwners = async () => {
    try {
      let url = `http://localhost:5000/owners?sort=${sortField}&order=${sortOrder}`;
      const res = await fetch(url, { credentials: "include" });
      const data = await res.json();
      setOwners(data);
    } catch (err) {
      console.error("Error fetching owners:", err);
      setOwners([]);
    }
  };

  const handleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  return (
    <div
      className="container"
      style={{ maxWidth: "1600px", margin: "auto", overflowX: "auto", padding: "20px" }}
    >
      {/* Header */}
      <div
        className="header-row"
        style={{ display: "flex", alignItems: "center", justifyContent: "space-between", marginBottom: "15px", flexWrap: "wrap" }}
      >
        <div className="header-left" style={{ display: "flex", gap: "10px", fontWeight: "bold" }}>
          <span>{currentDate}</span> | <span>Total Owners: {owners.length}</span>
        </div>
        <h2 style={{ margin: "0 auto", textAlign: "center", color: "black" }}>List of Owners</h2>
        <div className="header-right" style={{ fontWeight: "bold" }}>
          <a href="/" className="back-link" style={{ textDecoration: "none", color: "#333" }}>‚Üê Go Back Home</a>
        </div>
      </div>

      {/* Table */}
      {owners.length > 0 ? (
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            background: "white",
            borderRadius: "8px",
          }}
        >
          <thead>
            <tr>
              {columns.map((col) => {
                const isImageCol = ["eid_image", "passport_copy", "res_visa"].includes(col);
                return (
                  <th
                    key={col}
                    style={{
                      padding: "10px 12px",
                      borderBottom: "1px solid #ddd",
                      textAlign: "left",
                      fontSize: "0.9rem",
                      background: "#007BFF",
                      color: "white",
                      cursor: isImageCol ? "default" : "pointer",
                    }}
                    onClick={() => !isImageCol && handleSort(col)}
                  >
                    {col.replace("_", " ").toUpperCase()} {!isImageCol && sortField === col && `(${sortOrder})`}
                  </th>
                );
              })}
            </tr>
          </thead>
          <tbody>
            {owners.map((owner) => (
              <tr key={owner.owner_code} style={{ backgroundColor: "#fff" }}>
                {columns.map((key) => {
                  if (["eid_image", "passport_copy", "res_visa"].includes(key)) {
                    const imgUrl = owner[key]
                      ? `http://localhost:5000/owner_image/${owner.owner_code}/${key === "eid_image" ? "eid" : key === "passport_copy" ? "passport" : "visa"}`
                      : null;

                    return (
                      <td key={key} style={{ padding: "10px 12px", borderBottom: "1px solid #ddd" }}>
                        {imgUrl ? (
                          <img
                            src={imgUrl}
                            alt={key}
                            style={{ width: "30px", height: "30px", objectFit: "cover", borderRadius: "4px", border: "1px solid #ccc", cursor: "pointer" }}
                            onClick={() => setPreviewImage(imgUrl)}
                          />
                        ) : (
                          "N/A"
                        )}
                      </td>
                    );
                  } else {
                    return (
                      <td key={key} style={{ padding: "10px 12px", borderBottom: "1px solid #ddd", fontSize: "0.9rem" }}>
                        {owner[key]}
                      </td>
                    );
                  }
                })}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p style={{ textAlign: "center", marginTop: "20px" }}>No owners found</p>
      )}

      {/* Image Preview Modal */}
      {previewImage && (
        <div
          onClick={() => setPreviewImage(null)}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            background: "rgba(0,0,0,0.7)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 1000,
            cursor: "pointer"
          }}
        >
          <img
            src={previewImage}
            alt="Preview"
            style={{ maxWidth: "80%", maxHeight: "80%", borderRadius: "8px", border: "2px solid white" }}
          />
        </div>
      )}
    </div>
  );
};

export default ListOwners;
